class Rsuid {

    constructor() {

        this.extra_value = 0;

    }

    get(){

        let unique_time = new Date().getTime();
        this.extra_value += 1;
        return (unique_time + this.extra_value).toString(32);

    }

}

module.exports = rsuid = new Rsuid();